package edu.stanford.hivdb.hivfacts.extras;

import java.util.List;
import java.util.ArrayList;

import edu.stanford.hivdb.viruses.Gene;
import edu.stanford.hivdb.hivfacts.HIV;
import edu.stanford.hivdb.sequences.Sequence;
import edu.stanford.hivdb.testutils.SampleSubstitution;

public class HIV1Sample {

	private static final String originalPR = "CCTCAGATCACTCTTTGGCAACGACCCCTCGTCACAATAAAGATAGGGGGGCAACTAAAGGAAGCTCTATTAGATACAGGAGCAGATGATACAGTATTAGAAGAAATGAATTTGCCAGGAAGATGGAAACCAAAAATGATAGGGGGAATTGGAGGTTTTATCAAAGTAAGACAGTATGATCAGATACTCATAGAAATCTGTGGACATAAAGCTATAGGTACAGTATTAGTAGGACCTACACCTGTCAACATAATTGGAAGAAATCTGTTGACTCAGATTGGTTGCACTTTAAATTTT";
	private static final String originalRT = "";
	private static final String originalIN = "";
	private List<SampleSubstitution> subPR = new ArrayList<>();
	private List<SampleSubstitution> subRT = new ArrayList<>();
	private List<SampleSubstitution> subIN = new ArrayList<>();

	public HIV1Sample() {
		// do nothing
	}

	public void addSubstitutionRule(
			Gene<HIV> gene, int startPos, int endPos, String replaceTo) {
		SampleSubstitution sub =
			new SampleSubstitution(startPos, endPos, replaceTo);
		switch (gene.getAbstractGene()) {
			case "PR":
				subPR.add(sub);
				break;
			case "RT":
				subRT.add(sub);
				break;
			case "IN":
				subIN.add(sub);
				break;
		}
	}

	public void addSubstitutionRules(
			Gene<HIV>[] geneArr, int[] startPosArr,
			int[] endPosArr, String[] replaceToArr) {
		for (int i=0; i< geneArr.length; i ++) {
			addSubstitutionRule(
				geneArr[i], startPosArr[i], endPosArr[i], replaceToArr[i]);
		}
	}

	public void cleanSubstitutionRules(Gene<HIV> gene) {
		switch (gene.getAbstractGene()) {
			case "PR":
				subPR.clear();
				break;
			case "RT":
				subRT.clear();
				break;
			case "IN":
				subIN.clear();
				break;
		}
	}

	public String getSampleString() {
		return getPR() + getRT() + getIN();
	}

	public String getPR() {
		StringBuilder r = new StringBuilder(originalPR);
		for (SampleSubstitution sub: subPR) {
			r = sub.apply(r);
		}
		return r.toString();
	}

	public String getRT() {
		StringBuilder r = new StringBuilder(originalRT);
		for (SampleSubstitution sub: subRT) {
			r = sub.apply(r);
		}
		return r.toString();
	}

	public String getIN() {
		StringBuilder r = new StringBuilder(originalIN);
		for (SampleSubstitution sub: subIN) {
			r = sub.apply(r);
		}
		return r.toString();
	}

	public String getByGene(Gene<HIV> gene) {
		String r = null;
		switch (gene.getAbstractGene()) {
			case "PR":
				r = getPR();
				break;
			case "RT":
				r = getRT();
				break;
			case "IN":
				r = getIN();
				break;
		}
		return r;
	}

	public Sequence getSequence(String name) {
		return new Sequence(name, getSampleString());
	}

	public Sequence getSequence() {
		return getSequence("test");
	}
}
